import React, {useEffect, useState} from "react";
import "./css/style.css";

const Tempapp = () => {

    const [city, setCity] = useState(null);
    const[search, setSearch] = useState("Mumbai");

    useEffect( () => {
        const fetchApi = async () => {
            const url = `https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=49202f9cad5df6fe7c9df64f9839044a`
            const response = await fetch(url);
          
           const resJson = await response.json();
        //    console.log(resJson);
        setCity(resJson);
        }
        
        fetchApi();
    })
    return(
        <>
           <div className="box">
                <div className="inputData">
                    <input
                    type="search"
                    className="inputField" 
                   onChange= { (event) => {
                        setSearch(event)

                   }                    
                     }/>
                </div>
      

           <div className="info">
                <h2 className="location">
                             
                
                <i className="fa-solid fa-street-view fa-fade fa-xs" ></i> {search}
                </h2>
                <h1 className="temp">
                5.25Â°Cel
                </h1>
                <h3 className="tempmin_max"> Min : 5.25 Cel | Max : 5.25 Cel

                </h3>
           </div>
           
           <div className="wave -one">  </div>
           <div className="wave -two">  </div>
           <div className="wave -three">  </div>
           </div>
        </>
    )
}


export default Tempapp;
